mapper.select:
SELECT c.COMMUNITY_PK, c.COMMUNITY_USER_PK, c.COMMUNITY_CATEGORY, c.COMMUNITY_TITLE, c.COMMUNITY_CONTENT, c.COMMUNITY_VIEW, c.COMMUNITY_ISGOOD, c.COMMUNITY_REPORT, to_char(c.COMMUNITY_DATE,'yyyy-mm-dd') as COMMUNITY_DATE, u.* FROM COMMUNITY c, USERS u WHERE c.COMMUNITY_USER_PK = u.USER_PK AND c.COMMUNITY_REPORT <= 10
and c.community_category = #{category}
ORDER BY c.COMMUNITY_PK DESC

mapper.update:
update community set community_title = #{community_title},
community_category = #{community_category}
, community_content = #{community_content} where community_pk =
#{community_pk}
